import React, { useState } from 'react';
import DataWithAll from './DataWithAll';
import DataWithoutAll from './DatawithoutAll';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';


const OurPlans = () =>{
    const [current, setcurrent] = useState(0);
    const length = DataWithAll.length;

    if(!Array.isArray(DataWithAll) || length<=0){
        return null;
    }

    const NextBtn = () =>{
        setcurrent(current === length-1 ? 0 : current+1);
    }

    const PrevBtn = () =>{
        setcurrent(current === 0 ? length-1 : current-1);
    }
    return(
        <>
        <div className='plan'>
        <h3>Adventure + Meal + Hotel</h3>
        <div>
        <button onClick={NextBtn}><ArrowBackIosIcon/></button>
        <button onClick={PrevBtn}><ChevronRightIcon/></button>
        </div>
        </div>
        </>
    )    
}

export default OurPlans;
